import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import '../styles/Product.css';
import { productsData } from "../assets/products";

const Product = () => {
    const navigate = useNavigate();
    const location = useLocation();

    const queryParams = new URLSearchParams(location.search);
    const selectedCategory = queryParams.get("category");

    const [searchTerm, setSearchTerm] = useState("");

    const categories = [...new Set(productsData.map(product => product.category))];

    const filteredProducts = productsData.filter(product => {
        const matchesCategory = selectedCategory ? product.category === selectedCategory : true;
        const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
        return matchesCategory && matchesSearch;
    });

    const handleCategoryClick = (category) => {
        navigate(`/products?category=${category}`);
    };

    return (
        <div className="product-container">
            <h1 className="product-header">Products</h1>

            {/* Search Box */}
            <input
                type="text"
                placeholder="Search products..."
                className="search-box"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />

            {/* Category Section */}
            <div className="category-container">
                <button onClick={() => navigate("/products")} className="category-button">
                    All
                </button>
                {categories.map((category) => (
                    <button
                        key={category}
                        onClick={() => handleCategoryClick(category)}
                        className={`category-button ${selectedCategory === category ? "active" : ""}`}
                    >
                        {category}
                    </button>
                ))}
            </div>

            {/* Products List */}
            <div className="products">
                {filteredProducts.length > 0 ? (
                    filteredProducts.map((product) => (
                        <div key={product._id} className="product-item">
                            <img src={product.img} className="products-img" alt={product.name} />
                            <h2 className="products-name">{product.name}</h2>
                            <button onClick={() => navigate(`/products/${product._id}`)} className="products-button">
                                View Details
                            </button>
                        </div>
                    ))
                ) : (
                    <p className="no-products">No products found.</p>
                )}
            </div>
        </div>
    );
};

export default Product;
