import React from "react";
import { useParams } from "react-router-dom";

const ProductDetails = () => {
  const { id } = useParams();  // Get the product ID from the URL

  if (!product) {
    return <h2>Product not found</h2>; // Handle invalid ID case
  }

  return (
    <div className="product-details-container">
      <h1 className="product-details-header">{product.category}</h1>
      <img src={product.img} className="product-details-img" alt={product.category} />
      <p className="product-details-material"><strong>Material:</strong> {product.material}</p>
      <p className="product-details-description">{product.description}</p>
    </div>
  );
};

export default ProductDetails;
